mapper.select:

SELECT
    id,
    content,
    1 - (embedding <=> #{embedding, typeHandler=online.yuuu.aiknowledgehub.config.mp.PgVectorTypeHandler}) AS score
FROM document_chunks
WHERE knowledge_base_id = #{kbId}
ORDER BY embedding <=> #{embedding, typeHandler=online.yuuu.aiknowledgehub.config.mp.PgVectorTypeHandler}
LIMIT #{topK}
